var filters = {

  notWhitespace: function (currentValue) {
    var patt = /\S/;
    var passed = patt.test(currentValue);
    return passed;
  },

  // https://stackoverflow.com/a/46181
  onlyEmail: function (currentValue) {
    var patt = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    var passed = patt.test(currentValue);
    return passed;
  },

  onlyUnique: function (currentValue, index, arr) {
    var passed = arr.indexOf(currentValue) === index;
    return passed;
  },

  onlyAlphanumeric: function (currentValue){
    var patt = /^[a-z0-9]+$/i;
    var passed = patt.test(currentValue);
    return passed;
  },

  onlyStrings: function (currentValue){
    var passed = typeof(currentValue) === 'string';
    return passed;
  }
};

module.exports = filters;
