var winston = require('winston');
var fs = require('fs');
var logDir = 'log';
var shortid = require('shortid');

if(!fs.existsSync(logDir)) {
  fs.mkdirSync(logDir);
}

var tsFormat = () => (new Date()).toLocaleTimeString();

var logger = new (winston.Logger)({
  transports:[
    new(winston.transports.Console)({
      timestamp: tsFormat,
      colorize: true,
      level: process.env.LOG_LEVEL
    }),
    new(require('winston-daily-rotate-file'))({
      filename: logDir + '/' + process.env.NODE_ENV + '.log',
      timestamp: tsFormat,
      datePatter: 'yyyy-MM-dd',
      prepend: true,
      level: process.env.LOG_LEVEL
    })
  ]
});

logger.tag = function(){ 
  return function(req, res, next){
    req.logTag = '[' + shortid.generate() + '] ' + req.method + ' ' + req.originalUrl;
    logger.info(req.logTag);
  next();
  };
};


logger.verbose('Logger initialized for ' + process.env.NODE_ENV + ' environment');

module.exports = logger;
