var messages = {
  error: {
    unauthorized: {message: 'Permission denied'},
    unknown: {message: 'We\'re having trouble handling your request, please try again later'}
  },
  usage: {
    missingFields: {message: 'Please fill out all fields'},
    username: {message: 'Username must be 3-24 characters long and may only contain letters and numbers'},
    email: {message: 'Invalid email address'},
    password: {message: 'Password must be 4-128 characters long'}
  },
  conflict: {
    username: function(username){
      return {message: 'Sorry! Username ' + username + ' is taken'};
    },
    email: function(email){
      return {message: email + ' is already being used'};
    }
  },
  success: {
    newUser: function(username){
      return {message: 'New user registered: ' + username};
    }
  }
};

module.exports = messages;
