var filters = require('filters');

var validate = {

  username: function(username) {
    return Boolean(username)
      && filters.onlyStrings(username)
      && filters.onlyAlphanumeric(username)
      && username.length >= 3
      && username.length <= 24;
  },

  email: function(email) {
    return Boolean(email)
      && filters.onlyStrings(email)
      && filters.onlyEmail(email)
      && email.length <= 254;
  },

  password: function(password) {
    return Boolean(password)
      && filters.onlyStrings(password)
      && password.length >= 4
      && password.length <= 128;
  },

  title: function(title) {
    return Boolean(title)
      && filters.onlyStrings(title)
      && filters.notWhitespace(title)
      && title.length <= 128;
  },

  comment: function(comment) {
    return Boolean(comment)
      && filters.onlyStrings(comment)
      && comment.length <= 1000;
  },

  tag: function(tag) {
    return Boolean(tag)
      && filters.onlyStrings(tag)
      && filters.onlyAlphanumeric(tag)
      && tag.length <= 50;
  }
};

module.exports = validate;
